import * as THREE from 'three';
import Stats from 'three/addons/libs/stats.module.js';
import { GUI } from 'three/addons/libs/lil-gui.module.min.js';
import { OrbitControls } from 'three/addons/controls/OrbitControls.js';
export default {
  name: 'HelloWorld',
  data() {
    return {
      // Three.js 相关对象
      camera: null,
      scene: null,
      renderer: null,
      bulbLight: null,
      bulbMat: null,
      hemiLight: null,
      stats: null,
      // 性能监视器
      floorMat: null,
      cubeMat: null,
      ballMat: null,
      // 控制参数
      previousShadowMap: false,
      // 阴影映射控制
      params: {
        shadows: true,
        // 是否显示阴影
        exposure: 0.68,
        // 曝光度
        bulbPower: '800 lm (60W)',
        // 默认灯泡功率
        hemiIrradiance: '0.5 lx (Full Moon)' // 默认环境光照射度
      },
      // 灯泡功率映射表
      bulbLuminousPowers: {
        '110000 lm (1000W)': 110000,
        '3500 lm (300W)': 3500,
        '1700 lm (100W)': 1700,
        '800 lm (60W)': 800,
        '400 lm (40W)': 400,
        '180 lm (25W)': 180,
        '20 lm (4W)': 20,
        'Off': 0
      },
      // 半球光照射强度映射表
      hemiLuminousIrradiances: {
        '0.0001 lx (Moonless Night)': 0.0001,
        '0.002 lx (Night Airglow)': 0.002,
        '0.5 lx (Full Moon)': 0.5,
        '3.4 lx (City Twilight)': 3.4,
        '50 lx (Living Room)': 50,
        '100 lx (Very Overcast)': 100,
        '350 lx (Office Room)': 350,
        '400 lx (Sunrise/Sunset)': 400,
        '1000 lx (Overcast)': 1000,
        '18000 lx (Daylight)': 18000,
        '50000 lx (Direct Sun)': 50000
      }
    };
  },
  mounted() {
    this.init();
    this.animate();
  },
  methods: {
    init() {
      const container = this.$el.querySelector('#container');
      this.stats = new Stats();
      container.appendChild(this.stats.dom);
      this.camera = new THREE.PerspectiveCamera(50, window.innerWidth / window.innerHeight, 0.1, 100);
      this.camera.position.x = -4;
      this.camera.position.z = 4;
      this.camera.position.y = 2;
      this.scene = new THREE.Scene();

      // 灯泡几何和材料的创建
      const bulbGeometry = new THREE.SphereGeometry(0.02, 16, 8);
      this.bulbLight = new THREE.PointLight(0xffee88, 1, 100, 2);
      this.bulbMat = new THREE.MeshStandardMaterial({
        emissive: 0xffffee,
        emissiveIntensity: 1,
        color: 0x000000
      });
      this.bulbLight.add(new THREE.Mesh(bulbGeometry, this.bulbMat));
      this.bulbLight.position.set(0, 2, 0);
      this.bulbLight.castShadow = true;
      this.scene.add(this.bulbLight);

      // 其他光源和材料的设置...
      this.renderer = new THREE.WebGLRenderer();
      this.renderer.shadowMap.enabled = true;
      this.renderer.toneMapping = THREE.ReinhardToneMapping;
      this.renderer.setPixelRatio(window.devicePixelRatio);
      this.renderer.setSize(window.innerWidth, window.innerHeight);
      container.appendChild(this.renderer.domElement);
      const controls = new OrbitControls(this.camera, this.renderer.domElement);
      controls.minDistance = 1;
      controls.maxDistance = 20;
      window.addEventListener('resize', this.onWindowResize);

      // 材料和纹理
      this.floorMat = new THREE.MeshStandardMaterial({
        roughness: 0.8,
        color: 0xffffff,
        metalness: 0.2,
        bumpScale: 1
      });
      const textureLoader = new THREE.TextureLoader();
      textureLoader.load(require('@/assets/hardwood2_diffuse.jpg'), map => {
        map.wrapS = THREE.RepeatWrapping;
        map.wrapT = THREE.RepeatWrapping;
        map.anisotropy = 4;
        map.repeat.set(10, 24);
        map.colorSpace = THREE.SRGBColorSpace;
        this.floorMat.map = map;
        this.floorMat.needsUpdate = true;
      });
      textureLoader.load(require('@/assets/hardwood2_bump.jpg'), function (map) {
        map.wrapS = THREE.RepeatWrapping;
        map.wrapT = THREE.RepeatWrapping;
        map.anisotropy = 4;
        map.repeat.set(10, 24);
        this.floorMat.bumpMap = map;
        this.floorMat.needsUpdate = true;
      });
      textureLoader.load(require('@/assets/hardwood2_roughness.jpg'), function (map) {
        map.wrapS = THREE.RepeatWrapping;
        map.wrapT = THREE.RepeatWrapping;
        map.anisotropy = 4;
        map.repeat.set(10, 24);
        this.floorMat.roughnessMap = map;
        this.floorMat.needsUpdate = true;
      });
      this.cubeMat = new THREE.MeshStandardMaterial({
        roughness: 0.7,
        color: 0xffffff,
        bumpScale: 1,
        metalness: 0.2
      });
      textureLoader.load(require('@/assets/brick_diffuse.jpg'), function (map) {
        map.wrapS = THREE.RepeatWrapping;
        map.wrapT = THREE.RepeatWrapping;
        map.anisotropy = 4;
        map.repeat.set(1, 1);
        map.colorSpace = THREE.SRGBColorSpace;
        this.cubeMat.map = map;
        this.cubeMat.needsUpdate = true;
      });
      textureLoader.load(require('@/assets/brick_bump.jpg'), function (map) {
        map.wrapS = THREE.RepeatWrapping;
        map.wrapT = THREE.RepeatWrapping;
        map.anisotropy = 4;
        map.repeat.set(1, 1);
        this.cubeMat.bumpMap = map;
        this.cubeMat.needsUpdate = true;
      });
      this.ballMat = new THREE.MeshStandardMaterial({
        color: 0xffffff,
        roughness: 0.5,
        metalness: 1.0
      });
      textureLoader.load(require('@/assets/earth_atmos_2048.jpg'), function (map) {
        map.anisotropy = 4;
        map.colorSpace = THREE.SRGBColorSpace;
        this.ballMat.map = map;
        this.ballMat.needsUpdate = true;
      });
      textureLoader.load(require('@/assets/earth_specular_2048.jpg'), function (map) {
        map.anisotropy = 4;
        map.colorSpace = THREE.SRGBColorSpace;
        this.ballMat.metalnessMap = map;
        this.ballMat.needsUpdate = true;
      });

      // 几何体和网格
      const floorGeometry = new THREE.PlaneGeometry(20, 20);
      const floorMesh = new THREE.Mesh(floorGeometry, this.floorMat);
      floorMesh.receiveShadow = true;
      floorMesh.rotation.x = -Math.PI / 2.0;
      this.scene.add(floorMesh);
      const ballGeometry = new THREE.SphereGeometry(0.25, 32, 32);
      const ballMesh = new THREE.Mesh(ballGeometry, this.ballMat);
      ballMesh.position.set(1, 0.25, 1);
      ballMesh.rotation.y = Math.PI;
      ballMesh.castShadow = true;
      this.scene.add(ballMesh);
      const boxGeometry = new THREE.BoxGeometry(0.5, 0.5, 0.5);
      const boxMesh = new THREE.Mesh(boxGeometry, this.cubeMat);
      boxMesh.position.set(-0.5, 0.25, -1);
      boxMesh.castShadow = true;
      this.scene.add(boxMesh);
      const boxMesh2 = new THREE.Mesh(boxGeometry, this.cubeMat);
      boxMesh2.position.set(0, 0.25, -5);
      boxMesh2.castShadow = true;
      this.scene.add(boxMesh2);
      const boxMesh3 = new THREE.Mesh(boxGeometry, this.cubeMat);
      boxMesh3.position.set(7, 0.25, 0);
      boxMesh3.castShadow = true;
      this.scene.add(boxMesh3);

      // 窗口大小变化的事件处理
      window.addEventListener('resize', this.onWindowResize);

      // GUI 控制器
      const gui = new GUI();
      gui.add(this.params, 'hemiIrradiance', Object.keys(this.hemiLuminousIrradiances));
      gui.add(this.params, 'bulbPower', Object.keys(this.bulbLuminousPowers));
      gui.add(this.params, 'exposure', 0, 1);
      gui.add(this.params, 'shadows');
      gui.open();
    },
    animate() {
      requestAnimationFrame(this.animate);
      this.render();
    },
    render() {
      // 更新渲染器的曝光度
      this.renderer.toneMappingExposure = Math.pow(this.params.exposure, 5.0); // 适应非常明亮的场景
      this.renderer.shadowMap.enabled = this.params.shadows;
      this.bulbLight.castShadow = this.params.shadows;
      if (this.params.shadows !== this.previousShadowMap) {
        this.ballMat.needsUpdate = true;
        this.cubeMat.needsUpdate = true;
        this.floorMat.needsUpdate = true;
        this.previousShadowMap = this.params.shadows;
      }

      // 设置灯泡的功率和辐射强度
      this.bulbLight.power = this.bulbLuminousPowers[this.params.bulbPower];
      this.bulbMat.emissiveIntensity = this.bulbLight.intensity / Math.pow(0.02, 2.0); // 从强度转换为灯泡表面的辐照度

      this.hemiLight.intensity = this.hemiLuminousIrradiances[this.params.hemiIrradiance];

      // 动画或时间相关的更新
      const time = Date.now() * 0.0005;
      this.bulbLight.position.y = Math.cos(time) * 0.75 + 1.25;

      // 渲染场景和相机
      this.renderer.render(this.scene, this.camera);

      // 更新性能监视器
      this.stats.update();
    },
    onWindowResize() {
      this.camera.aspect = window.innerWidth / window.innerHeight;
      this.camera.updateProjectionMatrix();
      this.renderer.setSize(window.innerWidth, window.innerHeight);
    }
  }
};
